INSERT INTO `wp_options` VALUES (389,'_transient_wp_job_manager_addons_html','<ul class=\"products\"><li class=\"first post-1059 product type-product status-publish has-post-thumbnail product_cat-core-add-ons product_cat-forms product_cat-functionality pa_license-developer pa_license-personal virtual sold-individually taxable shipping-taxable purchasable product-type-variable product-cat-core-add-ons product-cat-forms product-cat-functionality has-default-attributes has-children instock\">\n	<a href=\"https://wpjobmanager.com/add-ons/applications/\">\n	</a><a href=\"https://wpjobmanager.com/add-ons/applications/\" class=\"item\">\n		<img width=\"318\" height=\"222\" src=\"https://wpjobmanager.com/wp-content/uploads/2014/06/applications.png?resize=318%2C222\" class=\"attachment-shop_catalog size-shop_catalog wp-post-image\" alt=\"applications\" /></a>\n\n	<h2>\n	<span class=\"price\"><span class=\"amount\">$39</span></span>\n <a href=\"https://wpjobmanager.com/add-ons/applications/\">Applications</a></h2>\n\n	<p>Allow candidates to apply to jobs using a form &amp; employers to view and manage the applications from their job dashboard.</p>\n</li>\n				\n					<li class=\"post-56 product type-product status-publish has-post-thumbnail product_cat-core-add-ons product_cat-selling pa_license-developer pa_license-personal virtual sold-individually taxable shipping-taxable purchasable product-type-variable product-cat-core-add-ons product-cat-selling has-default-attributes has-children instock\">\n	<a href=\"https://wpjobmanager.com/add-ons/wc-paid-listings/\">\n	</a><a href=\"https://wpjobmanager.com/add-ons/wc-paid-listings/\" class=\"item\">\n		<img width=\"318\" height=\"222\" src=\"https://wpjobmanager.com/wp-content/uploads/2014/02/wc_paid_small1.png?resize=318%2C222\" class=\"attachment-shop_catalog size-shop_catalog wp-post-image\" alt=\"wc_paid_small1\" /></a>\n\n	<h2>\n	<span class=\"price\"><span class=\"amount\">$39</span></span>\n <a href=\"https://wpjobmanager.